#!/bin/bash

# http://wiki.openttdcoop.org/Basecosts
# http://newgrf-specs.tt-wiki.net/wiki/BaseCosts
# NewBaseCost = OldBaseCost * 2^(n-8), where n is the value of property 08.

GRFID="4D 47 03 05"
FILENAME="basecosts"
MAXPARAMS=44
FACTDEF=10

. lib/hg.version.sh

NFO="sprites/$FILENAME.nfo"
[ ! -d "sprites" ] && mkdir sprites



cat >$NFO <<EOF
// Automatically generated by GRFCODEC. Do not modify!
// (Info version 7)
// Format: spritenum imagefile depth xpos ypos xsize ysize xrel yrel zoom flags
  0 * 4 00 00 00 00

 -1 * 0  14 "C" "INFO"
                  "B" "PALS" \w1 "A"
                  "B" "VRSN" \w4 \d$REVISION
                  "B" "MINV" \w4 \d0
                  "B" "NPAR" \w1 \b$MAXPARAMS
                  "T" "URL_" 7F "http://dev.openttdcoop.org/p/basecosts" 00
                  00
            00
EOF
PARAM=0
IFS=$'\n'
for i in $(cat data/keys); do
cat >>$NFO <<EOB
 -1 * 0  14 "C" "INFO"
                  "C" "PARA"
                      "C" \d$PARAM
                          "T" "NAME" 7F "$i" 00
                          "T" "DESC" 7F "$i" 00
                          "B" "TYPE" \w1 \b0
                          "B" "DFLT" \w4 \d$FACTDEF
                          "B" "LIMI" \w8 \d1 \d24
                          "C" "VALU"
`cat data/values`
                              00
                          00
                      00
                  00
            00
EOB
let PARAM+=1
done
cat >>$NFO <<EOF
  0 * 0	08 08 $GRFID "BaseCosts Mod $VERSION" 00
    "http://dev.openttdcoop.org/p/basecosts" 0D
    "$VERDATE / GPL / Ammler" 00
EOF
TYPE=0
FACT=1
for i in $(cat data/keys); do
cat >>$NFO <<EOB

// param$TYPE: $i
  0 * 0 0D
    \b$TYPE          // if Parameter $TYPE unset -> default $FACTDEF
    80 FF 00 \d$FACTDEF

  0 * 0 06 // Action6 (<param-num> <param-size> <offset>){n} FF
    \b$TYPE \b1 \b6  // Parameter $TYPE
    FF

// Action 0 will be modified by previous Action6
  0 * 0 00 // Action0 <Feature> <Num-props> <Num-info> <Id> (<Property <New-info>)...
    08          // Properties for general variables
    01 01       // 1 properity, 1 type (id)
    \b$TYPE     // ID (TYPE)
    08          // Basecosts
    00          // new value (FACT)

////////////////////////////////////////////////
EOB
let TYPE+=1
done

. lib/buildgrf.sh

exit


